import React, { useState, useEffect, useCallback } from 'react';
import { useApi } from '../../hooks/useApi';
import loanService from '../../services/loanService';
import LoanList from '../../components/Loans/LoanList';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import FilterPanel from '../../components/common/FilterPanel';
import StatsCard from '../../components/common/StatsCard';
import Toast from '../../components/common/Toast';
import ConfirmModal from '../../components/common/ConfirmModal';
import '../../styles/AdminLoansPage.css';

const AdminLoansPage = () => {
  const [loans, setLoans] = useState([]);
  const [filteredLoans, setFilteredLoans] = useState([]);
  const [filters, setFilters] = useState({
    status: '',
    readerId: '',
    bookId: '',
    startDate: '',
    endDate: '',
    overdue: false
  });
  const [toast, setToast] = useState({ show: false, message: '', type: '' });
  const [confirmModal, setConfirmModal] = useState({ show: false, action: null, data: null });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const { loading, error, execute } = useApi();

  // Cargar pr√©stamos
  const loadLoans = useCallback(async () => {
    try {
      const response = await execute(() => loanService.getAllLoans());
      if (response) {
        setLoans(response);
        applyFilters(response);
      }
    } catch (err) {
      console.error('Error loading loans:', err);
      showToast('Error al cargar pr√©stamos', 'error');
    }
  }, [execute]);

  // Aplicar filtros
  const applyFilters = useCallback((loansData) => {
    let filtered = [...loansData];

    if (filters.status) {
      filtered = filtered.filter(loan => loan.status === filters.status);
    }

    if (filters.readerId) {
      filtered = filtered.filter(loan => 
        loan.readerId?.toString().includes(filters.readerId) ||
        loan.reader?.name?.toLowerCase().includes(filters.readerId.toLowerCase())
      );
    }

    if (filters.bookId) {
      filtered = filtered.filter(loan => 
        loan.bookId?.toString().includes(filters.bookId) ||
        loan.book?.title?.toLowerCase().includes(filters.bookId.toLowerCase())
      );
    }

    if (filters.startDate) {
      filtered = filtered.filter(loan => 
        new Date(loan.createdAt) >= new Date(filters.startDate)
      );
    }

    if (filters.endDate) {
      filtered = filtered.filter(loan => 
        new Date(loan.createdAt) <= new Date(filters.endDate)
      );
    }

    if (filters.overdue) {
      filtered = filtered.filter(loan => loan.status === 'OVERDUE');
    }

    setFilteredLoans(filtered);
    setCurrentPage(1);
  }, [filters]);

  // Efectos
  useEffect(() => {
    loadLoans();
  }, [loadLoans]);

  useEffect(() => {
    applyFilters(loans);
  }, [filters, loans, applyFilters]);

  // Handlers
  const showToast = (message, type = 'success') => {
    setToast({ show: true, message, type });
  };

  const hideToast = () => {
    setToast({ show: false, message: '', type: '' });
  };

  const showConfirmModal = (action, data, message) => {
    setConfirmModal({
      show: true,
      action,
      data,
      message
    });
  };

  const hideConfirmModal = () => {
    setConfirmModal({ show: false, action: null, data: null });
  };

  const handleConfirmAction = async () => {
    const { action, data } = confirmModal;
    
    try {
      await execute(() => action(data));
      loadLoans();
      hideConfirmModal();
      
      const actionMessages = {
        [loanService.returnLoan]: 'Pr√©stamo devuelto exitosamente',
        [loanService.renewLoan]: 'Pr√©stamo renovado exitosamente',
        [loanService.cancelLoan]: 'Pr√©stamo cancelado exitosamente'
      };
      
      showToast(actionMessages[action] || 'Operaci√≥n exitosa', 'success');
    } catch (err) {
      console.error('Error executing action:', err);
      showToast('Error al ejecutar la operaci√≥n', 'error');
      hideConfirmModal();
    }
  };

  const handleReturnLoan = (loanId) => {
    showConfirmModal(
      loanService.returnLoan,
      loanId,
      '¬øEst√° seguro de que desea marcar este pr√©stamo como devuelto?'
    );
  };

  const handleRenewLoan = (loanId) => {
    showConfirmModal(
      loanService.renewLoan,
      loanId,
      '¬øEst√° seguro de que desea renovar este pr√©stamo?'
    );
  };

  const handleCancelLoan = (loanId) => {
    showConfirmModal(
      loanService.cancelLoan,
      loanId,
      '¬øEst√° seguro de que desea cancelar este pr√©stamo? Esta acci√≥n no se puede deshacer.'
    );
  };

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const resetFilters = () => {
    setFilters({
      status: '',
      readerId: '',
      bookId: '',
      startDate: '',
      endDate: '',
      overdue: false
    });
  };

  const exportLoans = () => {
    const csvContent = "data:text/csv;charset=utf-8," 
      + "ID,Lector,Libro,Fecha Pr√©stamo,Fecha Vencimiento,Estado\n"
      + filteredLoans.map(loan => 
          `${loan.id},${loan.reader?.name || 'N/A'},${loan.book?.title || 'N/A'},${loan.createdAt},${loan.dueDate},${loan.status}`
        ).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", `prestamos_${new Date().toISOString().split('T')[0]}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Calcular estad√≠sticas
  const stats = {
    total: filteredLoans.length,
    active: filteredLoans.filter(loan => loan.status === 'ACTIVE').length,
    overdue: filteredLoans.filter(loan => loan.status === 'OVERDUE').length,
    returned: filteredLoans.filter(loan => loan.status === 'RETURNED').length
  };

  // Paginaci√≥n
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentLoans = filteredLoans.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredLoans.length / itemsPerPage);

  const filterOptions = [
    {
      name: 'status',
      label: 'Estado',
      type: 'select',
      options: [
        { value: '', label: 'Todos' },
        { value: 'ACTIVE', label: 'Activos' },
        { value: 'RETURNED', label: 'Devueltos' },
        { value: 'OVERDUE', label: 'Vencidos' },
        { value: 'CANCELLED', label: 'Cancelados' }
      ]
    },
    {
      name: 'readerId',
      label: 'ID Lector',
      type: 'text',
      placeholder: 'Buscar por ID o nombre'
    },
    {
      name: 'bookId',
      label: 'ID Libro',
      type: 'text',
      placeholder: 'Buscar por ID o t√≠tulo'
    },
    {
      name: 'startDate',
      label: 'Fecha Desde',
      type: 'date'
    },
    {
      name: 'endDate',
      label: 'Fecha Hasta',
      type: 'date'
    },
    {
      name: 'overdue',
      label: 'Solo vencidos',
      type: 'checkbox'
    }
  ];

  if (loading && loans.length === 0) {
    return (
      <div className="admin-loans-page">
        <div className="loading-container">
          <LoadingSpinner />
          <p>Cargando pr√©stamos...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-loans-page">
      {/* Header */}
      <div className="page-header">
        <div className="header-content">
          <h1>Administrar Pr√©stamos</h1>
          <div className="header-actions">
            <button 
              onClick={exportLoans}
              className="btn btn-outline"
              disabled={filteredLoans.length === 0}
            >
              üìä Exportar CSV
            </button>
            <button 
              onClick={loadLoans}
              className="btn btn-primary"
              disabled={loading}
            >
              üîÑ Actualizar
            </button>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <StatsCard
          title="Total"
          value={stats.total}
          icon="üìö"
          color="blue"
        />
        <StatsCard
          title="Activos"
          value={stats.active}
          icon="‚úÖ"
          color="green"
        />
        <StatsCard
          title="Vencidos"
          value={stats.overdue}
          icon="‚ö†Ô∏è"
          color="red"
          highlight={stats.overdue > 0}
        />
        <StatsCard
          title="Devueltos"
          value={stats.returned}
          icon="üìñ"
          color="gray"
        />
      </div>

      {/* Filters */}
      <FilterPanel
        filters={filters}
        filterOptions={filterOptions}
        onFilterChange={handleFilterChange}
        onResetFilters={resetFilters}
        loading={loading}
      />

      {/* Error Message */}
      {error && (
        <div className="alert alert-error">
          <span className="alert-icon">‚ö†Ô∏è</span>
          <div className="alert-content">
            <strong>Error:</strong> {error}
            <button 
              onClick={loadLoans}
              className="btn btn-sm btn-outline"
            >
              Reintentar
            </button>
          </div>
        </div>
      )}

      {/* Results */}
      <div className="results-section">
        <div className="results-header">
          <p className="results-info">
            Mostrando {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredLoans.length)} de {filteredLoans.length} pr√©stamos
          </p>
          {loading && (
            <div className="inline-loading">
              <LoadingSpinner size="small" />
              <span>Actualizando...</span>
            </div>
          )}
        </div>

        {filteredLoans.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üìö</div>
            <h3>No hay pr√©stamos disponibles</h3>
            <p>No se encontraron pr√©stamos que coincidan con los filtros seleccionados.</p>
            {Object.values(filters).some(value => value) && (
              <button 
                onClick={resetFilters}
                className="btn btn-primary"
              >
                Limpiar Filtros
              </button>
            )}
          </div>
        ) : (
          <>
            <LoanList
              loans={currentLoans}
              onReturnLoan={handleReturnLoan}
              onRenewLoan={handleRenewLoan}
              onCancelLoan={handleCancelLoan}
              showAdminActions={true}
              loading={loading}
            />
            
            {/* Paginaci√≥n */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="btn btn-outline"
                >
                  ‚Üê Anterior
                </button>
                
                <div className="page-info">
                  P√°gina {currentPage} de {totalPages}
                </div>
                
                <button 
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="btn btn-outline"
                >
                  Siguiente ‚Üí
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Toast Notifications */}
      <Toast
        show={toast.show}
        message={toast.message}
        type={toast.type}
        onClose={hideToast}
      />

      {/* Confirm Modal */}
      <ConfirmModal
        show={confirmModal.show}
        message={confirmModal.message}
        onConfirm={handleConfirmAction}
        onCancel={hideConfirmModal}
        loading={loading}
      />
    </div>
  );
};

export default AdminLoansPage;
