import React, { useState, useEffect } from 'react';
import { useApi } from '../../hooks/useApi';
import readerService from '../../services/readerService';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import Layout from '../../components/common/Layout';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import '../../styles/AdminReadersPage.css';

const AdminReadersPage = () => {
  const [readers, setReaders] = useState([]);
  const [filteredReaders, setFilteredReaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedReader, setSelectedReader] = useState(null);
  const [filters, setFilters] = useState({
    status: 'all', // all, active, inactive
    searchType: 'all' // all, name, email, phone
  });
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    inactive: 0,
    withLoans: 0
  });

  const { handleApiCall } = useApi();
  const { user, isAdmin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    loadAllReaders();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [readers, filters, searchTerm]);

  const loadAllReaders = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await handleApiCall(readerService.getAllReaders);
      setReaders(response || []);
      calculateStats(response || []);
    } catch (err) {
      setError('Error al cargar lectores');
      console.error('Error loading readers:', err);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (readersData) => {
    const total = readersData.length;
    const active = readersData.filter(r => r.state).length;
    const inactive = total - active;
    const withLoans = readersData.filter(r => (r.activeLoans || 0) > 0).length;

    setStats({ total, active, inactive, withLoans });
  };

  const applyFilters = () => {
    let filtered = [...readers];

    // Filtro por término de búsqueda
    if (searchTerm.trim()) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(reader => {
        switch (filters.searchType) {
          case 'name':
            return reader.name?.toLowerCase().includes(term) || reader.lastName?.toLowerCase().includes(term);
          case 'email':
            return reader.email?.toLowerCase().includes(term);
          case 'phone':
            return false; // No hay campo phone en la entidad User
          default:
            return (
              reader.name?.toLowerCase().includes(term) ||
              reader.lastName?.toLowerCase().includes(term) ||
              reader.email?.toLowerCase().includes(term) ||
              reader.email?.toString().includes(term)
            );
        }
      });
    }

    // Filtro por estado
    if (filters.status !== 'all') {
      const isActive = filters.status === 'active';
      filtered = filtered.filter(reader => reader.state === isActive);
    }

    setFilteredReaders(filtered);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleReaderSelect = async (reader) => {
    try {
      setError('');
      const response = await handleApiCall(() => readerService.getReaderById(reader.id));
      setSelectedReader(response);
    } catch (err) {
      setError('Error al obtener detalles del lector');
      console.error('Error getting reader details:', err);
    }
  };

  const handleEditReader = (reader) => {
    alert('La funcionalidad de editar lectores no está disponible en este momento.');
  };

  const handleDeleteReader = async (readerId) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este lector? Esta acción no se puede deshacer.')) {
      try {
        await handleApiCall(() => readerService.deleteReader(readerId));
        loadAllReaders();
        setSelectedReader(null);
        alert('Lector eliminado exitosamente');
      } catch (err) {
        alert(`Error al eliminar lector: ${err.message}`);
      }
    }
  };

  const resetFilters = () => {
    setSearchTerm('');
    setFilters({ status: 'all', searchType: 'all' });
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No disponible';
    return new Date(dateString).toLocaleDateString('es-ES');
  };

  if (loading && readers.length === 0) {
    return (
      <Layout>
        <div className="admin-readers-page">
          <LoadingSpinner />
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="admin-readers-page">
        <div className="page-header">
          <h1>Administrar Lectores</h1>
          <div className="header-actions">
            <span className="total-readers">{stats.total} lectores registrados</span>
            <button 
              onClick={() => navigate('/admin/books')}
              className="btn btn-secondary"
            >
              ← Volver a Libros
            </button>
          </div>
        </div>

        {/* Estadísticas */}
        <div className="stats-section">
          <div className="stat-card">
            <h3>Total de Lectores</h3>
            <p className="stat-number">{stats.total}</p>
          </div>
          <div className="stat-card">
            <h3>Lectores Activos</h3>
            <p className="stat-number active">{stats.active}</p>
          </div>
          <div className="stat-card">
            <h3>Lectores Inactivos</h3>
            <p className="stat-number inactive">{stats.inactive}</p>
          </div>
          <div className="stat-card">
            <h3>Con Préstamos Activos</h3>
            <p className="stat-number loans">{stats.withLoans}</p>
          </div>
        </div>

        {/* Filtros y búsqueda */}
        <div className="filters-section">
          <div className="search-filters">
            <div className="search-group">
              <input
                type="text"
                placeholder="Buscar lectores..."
                value={searchTerm}
                onChange={handleSearchChange}
                className="search-input"
              />
              <select
                name="searchType"
                value={filters.searchType}
                onChange={handleFilterChange}
                className="search-type-select"
              >
                <option value="all">Buscar en todo</option>
                <option value="name">Solo nombre</option>
                <option value="email">Solo email</option>
              </select>
            </div>
            
            <div className="filter-group">
              <select
                name="status"
                value={filters.status}
                onChange={handleFilterChange}
                className="status-filter"
              >
                <option value="all">Todos los estados</option>
                <option value="active">Solo activos</option>
                <option value="inactive">Solo inactivos</option>
              </select>
            </div>

            <button onClick={resetFilters} className="btn btn-secondary">
              Limpiar Filtros
            </button>
          </div>
          
          <div className="results-info">
            Mostrando {filteredReaders.length} de {readers.length} lectores
          </div>
        </div>

        {error && (
          <div className="error-message">
            <p>Error: {error}</p>
          </div>
        )}

        {/* Lista de lectores */}
        <div className="readers-section">
          <div className="readers-list">
            {filteredReaders.length === 0 ? (
              <div className="no-readers">
                <div className="no-readers-icon">👥</div>
                <h3>No se encontraron lectores</h3>
                <p>No hay lectores que coincidan con los criterios de búsqueda</p>
                <p>Intenta ajustar los filtros o buscar con términos diferentes</p>
              </div>
            ) : (
              filteredReaders.map((reader) => (
                <div 
                  key={reader.id} 
                  className={`reader-card ${selectedReader?.id === reader.id ? 'selected' : ''}`}
                  onClick={() => handleReaderSelect(reader)}
                >
                  <div className="reader-info">
                    <h3>{reader.name} {reader.lastName}</h3>
                    <p><strong>Email:</strong> {reader.email}</p>
                    <p><strong>Nombre:</strong> {reader.name || 'No especificado'}</p>
                    <p><strong>Apellido:</strong> {reader.lastName || 'No especificado'}</p>
                  </div>
                  <div className="reader-status">
                    <span className={`status-badge ${reader.state ? 'active' : 'inactive'}`}>
                      {reader.state ? 'Activo' : 'Inactivo'}
                    </span>
                    <div className="reader-stats">
                      <span className="stat">📚 {reader.activeLoans || 0} préstamos</span>
                      <span className="stat">💰 {reader.pendingFines || 0} multas</span>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Detalles del lector seleccionado */}
          {selectedReader && (
            <div className="reader-details">
              <div className="details-header">
                <h3>Detalles del Lector</h3>
                <button 
                  onClick={() => setSelectedReader(null)}
                  className="close-btn"
                >
                  ✕
                </button>
              </div>
              
              <div className="details-content">
                <div className="detail-section">
                  <h4>Información Personal</h4>
                  <div className="detail-item">
                    <strong>Nombre:</strong> {selectedReader.name}
                  </div>
                  <div className="detail-item">
                    <strong>Email:</strong> {selectedReader.email}
                  </div>
                  <div className="detail-item">
                    <strong>Teléfono:</strong> {selectedReader.phone || 'No especificado'}
                  </div>
                  <div className="detail-item">
                    <strong>Dirección:</strong> {selectedReader.address || 'No especificada'}
                  </div>
                  <div className="detail-item">
                    <strong>Fecha de registro:</strong> {formatDate(selectedReader.registrationDate)}
                  </div>
                  <div className="detail-item">
                    <strong>Estado:</strong> 
                    <span className={`status-badge ${selectedReader.isActive || selectedReader.active ? 'active' : 'inactive'}`}>
                      {selectedReader.isActive || selectedReader.active ? 'Activo' : 'Inactivo'}
                    </span>
                  </div>
                </div>

                <div className="detail-section">
                  <h4>Estadísticas</h4>
                  <div className="detail-item">
                    <strong>Préstamos activos:</strong> {selectedReader.activeLoans || 0}
                  </div>
                  <div className="detail-item">
                    <strong>Total de préstamos:</strong> {selectedReader.totalLoans || 0}
                  </div>
                  <div className="detail-item">
                    <strong>Multas pendientes:</strong> {selectedReader.pendingFines || 0}
                  </div>
                  <div className="detail-item">
                    <strong>Total pagado en multas:</strong> ${selectedReader.totalFinesPaid || 0}
                  </div>
                </div>

                {selectedReader.currentLoans && selectedReader.currentLoans.length > 0 && (
                  <div className="detail-section">
                    <h4>Préstamos Actuales</h4>
                    <div className="current-loans">
                      {selectedReader.currentLoans.map((loan) => (
                        <div key={loan.id} className="loan-item">
                          <p><strong>{loan.book?.title}</strong></p>
                          <p>Vence: {formatDate(loan.returnDate)}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="details-actions">
                <button 
                  onClick={() => handleEditReader(selectedReader)}
                  className="btn btn-primary"
                >
                  Editar Lector
                </button>
                <button 
                  onClick={() => handleDeleteReader(selectedReader.id)}
                  className="btn btn-danger"
                >
                  Eliminar Lector
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
};

export default AdminReadersPage;
