import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/apiService';

const AuthTest = () => {
  const { user, login, isAdmin } = useAuth();
  const [testEmail, setTestEmail] = useState('');
  const [testPassword, setTestPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleTestLogin = async () => {
    try {
      setMessage('Probando login...');
      const result = await login(testEmail, testPassword);
      if (result.success) {
        setMessage('Login exitoso!');
      } else {
        setMessage(`Error: ${result.error}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };



  return (
    <div style={{ padding: '20px', backgroundColor: '#f5f5f5', margin: '20px', borderRadius: '8px' }}>
      <h3>Prueba de Autenticación</h3>
      
      <div style={{ marginBottom: '20px' }}>
        <h4>Usuario Actual:</h4>
        <p><strong>Email:</strong> {user?.email || 'No autenticado'}</p>
        <p><strong>Rol ID:</strong> {user?.roleId || 'No disponible'}</p>
        <p><strong>Es Admin:</strong> {isAdmin ? 'Sí' : 'No'}</p>
        <p><strong>Token:</strong> {localStorage.getItem('token') ? 'Presente' : 'Ausente'}</p>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h4>Login con tu Usuario Admin:</h4>
        <div style={{ marginBottom: '10px' }}>
          <input
            type="email"
            placeholder="Email"
            value={testEmail}
            onChange={(e) => setTestEmail(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <input
            type="password"
            placeholder="Password"
            value={testPassword}
            onChange={(e) => setTestPassword(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <button onClick={handleTestLogin}>
            Login
          </button>
        </div>
      </div>

      {message && (
        <div style={{ 
          padding: '10px', 
          backgroundColor: message.includes('Error') ? '#ffebee' : '#e8f5e8',
          borderRadius: '4px',
          marginTop: '10px'
        }}>
          {message}
        </div>
      )}
    </div>
  );
};

export default AuthTest;
