import api from './api';

export const loanService = {
  // Obtener todos los préstamos
  getLoans: async () => {
    try {
      const response = await api.get('/loans');
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Obtener préstamos por usuario
  getLoansByUser: async (userId) => {
    try {
      const response = await api.get(`/loans/user/${userId}`);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Obtener préstamo por ID
  getLoanById: async (loanId) => {
    try {
      const response = await api.get(`/loans/${loanId}`);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Crear nuevo préstamo
  createLoan: async (loanData) => {
    try {
      const response = await api.post('/loans', loanData);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Actualizar préstamo
  updateLoan: async (loanId, loanData) => {
    try {
      const response = await api.put(`/loans/${loanId}`, loanData);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Devolver libro
  returnBook: async (loanId) => {
    try {
      const response = await api.put(`/loans/${loanId}/return`);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Renovar préstamo
  renewLoan: async (loanId) => {
    try {
      const response = await api.put(`/loans/${loanId}/renew`);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Eliminar préstamo
  deleteLoan: async (loanId) => {
    try {
      const response = await api.delete(`/loans/${loanId}`);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // Obtener préstamos vencidos
  getOverdueLoans: async () => {
    try {
      const response = await api.get('/loans/overdue');
      return response;
    } catch (error) {
      throw error;
    }
  }
};
