import React, { useEffect } from 'react';
import '../styles/HomePage.css';

const HomePage = () => {
  useEffect(() => {
    // Efecto parallax suave para las formas flotantes
    const shapes = document.querySelectorAll('.shape');

    const handleScroll = () => {
      const scrolled = window.pageYOffset;
      shapes.forEach((shape, index) => {
        const speed = (index + 1) * 0.1;
        shape.style.transform = `translateY(${scrolled * speed}px)`;
      });
    };

    window.addEventListener('scroll', handleScroll);

    // Animación de aparición al hacer scroll
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver(function (entries) {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.style.opacity = '1';
          entry.target.style.transform = 'translateY(0)';
        }
      });
    }, observerOptions);

    document.querySelectorAll('.feature-card, .stat-card, .step').forEach(el => {
      el.style.opacity = '0';
      el.style.transform = 'translateY(30px)';
      el.style.transition = 'all 0.6s ease';
      observer.observe(el);
    });

    // Efecto de hover para botones
    const buttons = document.querySelectorAll('.btn');
    buttons.forEach(btn => {
      btn.addEventListener('mouseenter', () => {
        btn.style.transform = 'translateY(-3px) scale(1.05)';
      });
      btn.addEventListener('mouseleave', () => {
        btn.style.transform = 'translateY(0) scale(1)';
      });
    });

    // Cleanup
    return () => {
      window.removeEventListener('scroll', handleScroll);
      observer.disconnect();
    };
  }, []);

  return (
    <div>
      <div className="floating-shapes">
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
      </div>

      <div className="container">
        <header className="header">
          <h1>Bienvenido a la Biblioteca Digital</h1>
          <p>Descubre un mundo de conocimiento al alcance de tus manos</p>
          <div className="auth-buttons">
            <a href="#" className="btn btn-primary">INICIAR SESIÓN</a>
            <a href="#" className="btn btn-secondary">REGISTRARSE</a>
          </div>
        </header>

        <section>
          <h2 className="section-title">¿Por qué elegir nuestra biblioteca?</h2>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">📚</div>
              <h3>Amplio Catálogo</h3>
              <p>Miles de libros disponibles en diferentes géneros y formatos para satisfacer todos los gustos literarios</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">🔍</div>
              <h3>Búsqueda Avanzada</h3>
              <p>Encuentra fácilmente el libro que buscas con nuestros filtros inteligentes y sistema de recomendaciones</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">🕒</div>
              <h3>Disponible 24/7</h3>
              <p>Accede a tu biblioteca personal en cualquier momento desde cualquier dispositivo con conexión a internet</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">👥</div>
              <h3>Comunidad</h3>
              <p>Forma parte de una comunidad de lectores apasionados, comparte opiniones y descubre nuevas recomendaciones</p>
            </div>
          </div>
        </section>

        <section>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number pulse">1,500+</div>
              <div className="stat-label">Libros Disponibles</div>
            </div>

            <div className="stat-card">
              <div className="stat-number pulse">500+</div>
              <div className="stat-label">Usuarios Activos</div>
            </div>

            <div className="stat-card">
              <div className="stat-number pulse">50+</div>
              <div className="stat-label">Géneros</div>
            </div>

            <div className="stat-card">
              <div className="stat-number pulse">24/7</div>
              <div className="stat-label">Soporte</div>
            </div>
          </div>
        </section>

        <section className="how-it-works">
          <h2 className="section-title">¿Cómo funciona?</h2>
          <div className="steps-container">
            <div className="step">
              <div className="step-number">1</div>
              <h3>Regístrate</h3>
              <p>Crea tu cuenta gratuita en segundos y accede a todo nuestro catálogo de libros digitales</p>
            </div>

            <div className="step">
              <div className="step-number">2</div>
              <h3>Busca</h3>
              <p>Explora nuestro catálogo y encuentra tus libros favoritos usando nuestros filtros avanzados</p>
            </div>

            <div className="step">
              <div className="step-number">3</div>
              <h3>Solicita</h3>
              <p>Pide prestado el libro que quieres leer con un simple clic y comienza tu aventura literaria</p>
            </div>

            <div className="step">
              <div className="step-number">4</div>
              <h3>Disfruta</h3>
              <p>Lee y disfruta tu libro durante el período de préstamo desde cualquier dispositivo</p>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default HomePage;
