import React, { useState, useEffect, useCallback } from 'react';
import { useApi } from '../../hooks/useApi';
import fineService from '../../services/fineService';
import FineList from '../../components/fines/FineList';
import FineForm from '../../components/fines/FineForm';
import LoadingSpinner from '../../components/common/LoadingSpinner';
import FilterPanel from '../../components/common/FilterPanel';
import StatsCard from '../../components/common/StatsCard';
import Toast from '../../components/common/Toast';
import ConfirmModal from '../../components/common/ConfirmModal';
import Modal from '../../components/common/Modal';
import '../../styles/AdminFinesPage.css';

const AdminFinesPage = () => {
  const [fines, setFines] = useState([]);
  const [filteredFines, setFilteredFines] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedFine, setSelectedFine] = useState(null);
  const [filters, setFilters] = useState({
    status: '',
    readerId: '',
    startDate: '',
    endDate: '',
    minAmount: '',
    maxAmount: ''
  });
  const [toast, setToast] = useState({ show: false, message: '', type: '' });
  const [confirmModal, setConfirmModal] = useState({ show: false, action: null, data: null });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const { loading, error, execute } = useApi();

  // Cargar multas
  const loadFines = useCallback(async () => {
    try {
      const response = await execute(() => fineService.getAllFines());
      if (response) {
        setFines(response);
        applyFilters(response);
      }
    } catch (err) {
      console.error('Error loading fines:', err);
      showToast('Error al cargar multas', 'error');
    }
  }, [execute]);

  // Aplicar filtros
  const applyFilters = useCallback((finesData) => {
    let filtered = [...finesData];

    if (filters.status) {
      filtered = filtered.filter(fine => fine.status === filters.status);
    }

    if (filters.readerId) {
      filtered = filtered.filter(fine => 
        fine.readerId?.toString().includes(filters.readerId) ||
        fine.reader?.name?.toLowerCase().includes(filters.readerId.toLowerCase())
      );
    }

    if (filters.startDate) {
      filtered = filtered.filter(fine => 
        new Date(fine.createdAt) >= new Date(filters.startDate)
      );
    }

    if (filters.endDate) {
      filtered = filtered.filter(fine => 
        new Date(fine.createdAt) <= new Date(filters.endDate)
      );
    }

    if (filters.minAmount) {
      filtered = filtered.filter(fine => fine.amount >= parseFloat(filters.minAmount));
    }

    if (filters.maxAmount) {
      filtered = filtered.filter(fine => fine.amount <= parseFloat(filters.maxAmount));
    }

    setFilteredFines(filtered);
    setCurrentPage(1);
  }, [filters]);

  // Efectos
  useEffect(() => {
    loadFines();
  }, [loadFines]);

  useEffect(() => {
    applyFilters(fines);
  }, [filters, fines, applyFilters]);

  // Handlers
  const showToast = (message, type = 'success') => {
    setToast({ show: true, message, type });
  };

  const hideToast = () => {
    setToast({ show: false, message: '', type: '' });
  };

  const showConfirmModal = (action, data, message) => {
    setConfirmModal({
      show: true,
      action,
      data,
      message
    });
  };

  const hideConfirmModal = () => {
    setConfirmModal({ show: false, action: null, data: null });
  };

  const handleConfirmAction = async () => {
    const { action, data } = confirmModal;
    
    try {
      await execute(() => action(data));
      loadFines();
      hideConfirmModal();
      
      const actionMessages = {
        [fineService.payFine]: 'Multa pagada exitosamente',
        [fineService.deleteFine]: 'Multa eliminada exitosamente'
      };
      
      showToast(actionMessages[action] || 'Operaci√≥n exitosa', 'success');
    } catch (err) {
      console.error('Error executing action:', err);
      showToast('Error al ejecutar la operaci√≥n', 'error');
      hideConfirmModal();
    }
  };

  const handleCreateFine = async (fineData) => {
    try {
      await execute(() => fineService.createFine(fineData));
      setShowForm(false);
      loadFines();
      showToast('Multa creada exitosamente', 'success');
    } catch (err) {
      console.error('Error creating fine:', err);
      showToast('Error al crear la multa', 'error');
    }
  };

  const handleUpdateFine = async (fineData) => {
    try {
      await execute(() => fineService.updateFine(selectedFine.id, fineData));
      setShowForm(false);
      setSelectedFine(null);
      loadFines();
      showToast('Multa actualizada exitosamente', 'success');
    } catch (err) {
      console.error('Error updating fine:', err);
      showToast('Error al actualizar la multa', 'error');
    }
  };

  const handlePayFine = (fineId) => {
    showConfirmModal(
      fineService.payFine,
      fineId,
      '¬øEst√° seguro de que desea marcar esta multa como pagada?'
    );
  };

  const handleDeleteFine = (fineId) => {
    showConfirmModal(
      fineService.deleteFine,
      fineId,
      '¬øEst√° seguro de que desea eliminar esta multa? Esta acci√≥n no se puede deshacer.'
    );
  };

  const handleEditFine = (fine) => {
    setSelectedFine(fine);
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setSelectedFine(null);
  };

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const resetFilters = () => {
    setFilters({
      status: '',
      readerId: '',
      startDate: '',
      endDate: '',
      minAmount: '',
      maxAmount: ''
    });
  };

  const exportFines = () => {
    const csvContent = "data:text/csv;charset=utf-8," 
      + "ID,Lector,Monto,Raz√≥n,Fecha,Estado,Fecha Pago\n"
      + filteredFines.map(fine => 
          `${fine.id},${fine.reader?.name || 'N/A'},${fine.amount},${fine.reason || 'N/A'},${fine.createdAt},${fine.status},${fine.paidAt || 'N/A'}`
        ).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", `multas_${new Date().toISOString().split('T')[0]}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  // Calcular estad√≠sticas
  const stats = {
    total: filteredFines.length,
    pending: filteredFines.filter(fine => fine.status === 'PENDING').length,
    paid: filteredFines.filter(fine => fine.status === 'PAID').length,
    totalAmount: filteredFines.reduce((sum, fine) => sum + (fine.amount || 0), 0)
  };

  // Paginaci√≥n
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentFines = filteredFines.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredFines.length / itemsPerPage);

  const filterOptions = [
    {
      name: 'status',
      label: 'Estado',
      type: 'select',
      options: [
        { value: '', label: 'Todos' },
        { value: 'PENDING', label: 'Pendiente' },
        { value: 'PAID', label: 'Pagada' },
        { value: 'CANCELLED', label: 'Cancelada' }
      ]
    },
    {
      name: 'readerId',
      label: 'ID Lector',
      type: 'text',
      placeholder: 'Buscar por ID o nombre'
    },
    {
      name: 'startDate',
      label: 'Fecha Desde',
      type: 'date'
    },
    {
      name: 'endDate',
      label: 'Fecha Hasta',
      type: 'date'
    },
    {
      name: 'minAmount',
      label: 'Monto M√≠nimo',
      type: 'number',
      placeholder: '0.00'
    },
    {
      name: 'maxAmount',
      label: 'Monto M√°ximo',
      type: 'number',
      placeholder: '0.00'
    }
  ];

  if (loading && fines.length === 0) {
    return (
      <div className="admin-fines-page">
        <div className="loading-container">
          <LoadingSpinner />
          <p>Cargando multas...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-fines-page">
      {/* Header */}
      <div className="page-header">
        <div className="header-content">
          <h1>Administrar Multas</h1>
          <div className="header-actions">
            <button 
              onClick={exportFines}
              className="btn btn-outline"
              disabled={filteredFines.length === 0}
            >
              üìä Exportar CSV
            </button>
            <button 
              onClick={loadFines}
              className="btn btn-secondary"
              disabled={loading}
            >
              üîÑ Actualizar
            </button>
            <button 
              onClick={() => setShowForm(true)}
              className="btn btn-primary"
            >
              + Nueva Multa
            </button>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <StatsCard
          title="Total Multas"
          value={stats.total}
          icon="üìã"
          color="blue"
        />
        <StatsCard
          title="Pendientes"
          value={stats.pending}
          icon="‚è≥"
          color="orange"
          highlight={stats.pending > 0}
        />
        <StatsCard
          title="Pagadas"
          value={stats.paid}
          icon="‚úÖ"
          color="green"
        />
        <StatsCard
          title="Monto Total"
          value={`$${stats.totalAmount.toFixed(2)}`}
          icon="üí∞"
          color="purple"
        />
      </div>

      {/* Filters */}
      <FilterPanel
        filters={filters}
        filterOptions={filterOptions}
        onFilterChange={handleFilterChange}
        onResetFilters={resetFilters}
        loading={loading}
      />

      {/* Error Message */}
      {error && (
        <div className="alert alert-error">
          <span className="alert-icon">‚ö†Ô∏è</span>
          <div className="alert-content">
            <strong>Error:</strong> {error}
            <button 
              onClick={loadFines}
              className="btn btn-sm btn-outline"
            >
              Reintentar
            </button>
          </div>
        </div>
      )}

      {/* Results */}
      <div className="results-section">
        <div className="results-header">
          <p className="results-info">
            Mostrando {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredFines.length)} de {filteredFines.length} multas
          </p>
          {loading && (
            <div className="inline-loading">
              <LoadingSpinner size="small" />
              <span>Actualizando...</span>
            </div>
          )}
        </div>

        {filteredFines.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üí∞</div>
            <h3>No hay multas disponibles</h3>
            <p>No se encontraron multas que coincidan con los filtros seleccionados.</p>
            {Object.values(filters).some(value => value) && (
              <button 
                onClick={resetFilters}
                className="btn btn-primary"
              >
                Limpiar Filtros
              </button>
            )}
          </div>
        ) : (
          <>
            <FineList
              fines={currentFines}
              onPayFine={handlePayFine}
              onEditFine={handleEditFine}
              onDeleteFine={handleDeleteFine}
              showAdminActions={true}
              loading={loading}
            />
            
            {/* Paginaci√≥n */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="btn btn-outline"
                >
                  ‚Üê Anterior
                </button>
                
                <div className="page-info">
                  P√°gina {currentPage} de {totalPages}
                </div>
                
                <button 
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="btn btn-outline"
                >
                  Siguiente ‚Üí
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {/* Form Modal */}
      <Modal
        show={showForm}
        onClose={handleCloseForm}
        title={selectedFine ? 'Editar Multa' : 'Nueva Multa'}
        size="md"
      >
        <FineForm
          onSubmit={selectedFine ? handleUpdateFine : handleCreateFine}
          initialData={selectedFine}
          loading={loading}
          onCancel={handleCloseForm}
        />
      </Modal>

      {/* Toast Notifications */}
      <Toast
        show={toast.show}
        message={toast.message}
        type={toast.type}
        onClose={hideToast}
      />

      {/* Confirm Modal */}
      <ConfirmModal
        show={confirmModal.show}
        message={confirmModal.message}
        onConfirm={handleConfirmAction}
        onCancel={hideConfirmModal}
        loading={loading}
      />
    </div>
  );
};

export default AdminFinesPage;
