import React, { useState } from 'react';
import '../../styles/BookForm.css';

const BookForm = ({ onSubmit, loading = false, initialData = null }) => {
  const [formData, setFormData] = useState({
    title: initialData?.title || '',
    author: initialData?.author || '',
    image: initialData?.image || '',
    type: initialData?.type || ''
  });
  
  const [errors, setErrors] = useState({});
  const [imagePreview, setImagePreview] = useState(initialData?.image || '');

  // Tipos de libros según el requerimiento
  const bookTypes = [
    { value: '', label: 'Seleccione un tipo' },
    { value: 'educacion', label: 'Educación' },
    { value: 'novela', label: 'Novela' },
    { value: 'aventura', label: 'Aventura' },
    { value: 'poemas', label: 'Poemas' },
    { value: 'ciencia', label: 'Ciencia' },
    { value: 'historia', label: 'Historia' },
    { value: 'arte', label: 'Arte' },
    { value: 'tecnologia', label: 'Tecnología' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Limpiar error cuando el usuario empieza a escribir
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    
    if (file) {
      // Validar tipo de archivo
      if (!file.type.startsWith('image/')) {
        setErrors(prev => ({
          ...prev,
          image: 'Por favor seleccione un archivo de imagen válido'
        }));
        return;
      }

      // Validar tamaño (máximo 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setErrors(prev => ({
          ...prev,
          image: 'La imagen no debe superar los 5MB'
        }));
        return;
      }

      const reader = new FileReader();
      
      reader.onload = (event) => {
        const base64String = event.target.result;
        setFormData(prev => ({
          ...prev,
          image: base64String
        }));
        setImagePreview(base64String);
        
        // Limpiar error si existe
        if (errors.image) {
          setErrors(prev => ({
            ...prev,
            image: ''
          }));
        }
      };

      reader.onerror = () => {
        setErrors(prev => ({
          ...prev,
          image: 'Error al procesar la imagen'
        }));
      };

      reader.readAsDataURL(file);
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'El título es requerido';
    }
    
    if (!formData.author.trim()) {
      newErrors.author = 'El autor es requerido';
    }
    
    if (!formData.image.trim()) {
      newErrors.image = 'La imagen es requerida';
    }
    
    if (!formData.type) {
      newErrors.type = 'El tipo de libro es requerido';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      onSubmit(formData);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      author: '',
      image: '',
      type: ''
    });
    setImagePreview('');
    setErrors({});
  };

  return (
    <div className="book-form-container">
      <div className="form-header">
        <h2>{initialData ? 'Editar Libro' : 'Crear Nuevo Libro'}</h2>
        <p>Complete todos los campos requeridos para {initialData ? 'actualizar' : 'agregar'} el libro</p>
      </div>
      
      <div className="book-form" onSubmit={handleSubmit}>
        <div className="form-section">
          <h3>Información del Libro</h3>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="title">Título *</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className={errors.title ? 'error' : ''}
                placeholder="Ingrese el título del libro"
                maxLength="200"
              />
              {errors.title && <span className="error-message">{errors.title}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="author">Autor *</label>
              <input
                type="text"
                id="author"
                name="author"
                value={formData.author}
                onChange={handleChange}
                className={errors.author ? 'error' : ''}
                placeholder="Ingrese el nombre del autor"
                maxLength="150"
              />
              {errors.author && <span className="error-message">{errors.author}</span>}
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="image">Imagen del Libro *</label>
              <input
                type="file"
                id="image"
                name="image"
                accept="image/*"
                onChange={handleImageChange}
                className={errors.image ? 'error' : ''}
              />
              <small>Formatos permitidos: JPG, PNG, GIF. Tamaño máximo: 5MB</small>
              {errors.image && <span className="error-message">{errors.image}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="type">Tipo de Libro *</label>
              <select
                id="type"
                name="type"
                value={formData.type}
                onChange={handleChange}
                className={errors.type ? 'error' : ''}
              >
                {bookTypes.map(type => (
                  <option key={type.value} value={type.value}>{type.label}</option>
                ))}
              </select>
              {errors.type && <span className="error-message">{errors.type}</span>}
            </div>
          </div>

          {imagePreview && (
            <div className="form-group">
              <label>Vista Previa de la Imagen</label>
              <div className="image-preview">
                <img 
                  src={imagePreview} 
                  alt="Vista previa" 
                  style={{ maxWidth: '200px', maxHeight: '200px', objectFit: 'cover' }}
                />
              </div>
            </div>
          )}
        </div>

        <div className="form-actions">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={resetForm}
            disabled={loading}
          >
            Limpiar Formulario
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Guardando...
              </>
            ) : (
              initialData ? 'Actualizar Libro' : 'Crear Libro'
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default BookForm;
